import React from 'react';
import SearchBar from "./SearchBar";
import youtube from '../api/youtube';
import VideoList from './VideoList';
import VideoDetail from './VideoDetail'


// It will use state so it is a class app
class App extends React.Component{
    state = { videos: [], selectedVideo: null};

    // Async function to wait for the api response
    onTermSubmit = async (term) => {
        const response = await youtube.get('/search', {
            params: {
                q: term
            }
        });
        // Update both our states, current vid & list
        this.setState({
            videos: response.data.items,
            selectedVideo: response.data.items[0]
        });
    };

    // Nested callback function to swap the current playing video
    // It is sent down as a props see line in render()
    onVideoSelect = (video) => {
        this.setState({selectedVideo: video});
    };

    render(){
        return(
            // classnames from semantic-ui
            <div className = "ui container">
                <SearchBar onFormSubmit = {this.onTermSubmit}/>
                <div className = "ui grid">
                    <div className = "ui row"> 
                        <div className = "eleven wide column">
                        <VideoDetail video = {this.state.selectedVideo} />
                        </div>
                        <div className = "five wide column">
                            <VideoList 
                                onVideoSelect={this.onVideoSelect} 
                                videos = {this.state.videos}
                            />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
};

export default App;
